mapper.select:
select o.code as code, o.sub_date as sub_date, o.ordergoods_id as ordergoods_id, o.status as status, o.total_price as total_price, p.name as "ordergoodsProduct.product_name", p.cost_price as "ordergoodsProduct.product_price", od.unit as "ordergoodsProduct.unit", od.apply_num as "ordergoodsProduct.apply_num", od.update_num as "ordergoodsProduct.update_num"
from ordergoods o left join ordergoods_product od on o.code=od.ordergoods_code inner join product p on p.product_uuid=od.product_uuid
where o.shop_man=#{shopMan}
and o.status = #{status}
and o.sub_date >= #{beginTime}
and o.sub_date <= #{endTime}
order by o.ordergoods_id desc

select o.code as code, o.ordergoods_id as ordergoods_id, o.sub_date as sub_date, o.status as status, o.total_price as total_price, o.my_status as my_status, s.code as shop_code, s.name as shop_name, p.name as "ordergoodsProduct.product_name", p.cost_price as "ordergoodsProduct.product_price", od.unit as "ordergoodsProduct.unit", od.apply_num as "ordergoodsProduct.apply_num", od.update_num as "ordergoodsProduct.update_num"
from ordergoods o left join shop s on o.shop_id=s.shop_id left join ordergoods_product od on o.code=od.ordergoods_code inner join product p on p.product_uuid=od.product_uuid
where o.my_status = #{myStatus}
and s.name like CONCAT('%',#{shopName},'%')
and o.sub_date >= #{beginTime}
and o.sub_date <= #{endTime}
order by o.sub_date desc

select o.code as code, o.sub_date as sub_date, o.status as status, o.total_price as total_price, o.my_status as my_status, o.ordergoods_id as ordergoods_id, s.code as shop_code, s.name as shop_name, p.name as "ordergoodsProduct.product_name", p.cost_price as "ordergoodsProduct.product_price", od.unit as "ordergoodsProduct.unit", od.apply_num as "ordergoodsProduct.apply_num", od.update_num as "ordergoodsProduct.update_num"
from ordergoods o left join shop s on o.shop_id=s.shop_id left join ordergoods_product od on o.code=od.ordergoods_code inner join product p on p.product_uuid=od.product_uuid
where
o.my_status >=-100 and o.my_status!=0
o.my_status = #{myStatus}
o.my_status <= #{myStatus}
and s.name like CONCAT('%',#{shopName},'%')
and o.sub_date >= #{beginTime}
and o.sub_date <= #{endTime}
order by o.sub_date desc
