mapper.select:
SELECT s.nick_name as nick_name ,s.name as name ,s.shop_id as shop_id, s.address as address, s.status as status, s.code as code, n.province as province, n.city as city, n.district as district, n.street as street FROM shop s , nation n WHERE s.create_by_adm=#{createByAdm} AND (s.province=n.id AND s.city=n.id AND s.district=n.id OR s.street=n.id)
AND s.district=#{district}
UNION ALL
SELECT s.nick_name as nick_name ,s.name AS NAME ,s.shop_id AS shop_id, s.address AS address, s.status AS STATUS, s.code AS CODE, n.province AS province, na.city AS city, nat.district AS district, n.street AS street FROM shop s , nation n , nation na, nation nat WHERE s.create_by_adm=#{createByAdm} AND s.street IS NULL AND s.province=n.id AND s.city=na.id AND s.district=nat.id
AND s.district=#{district}
