mapper.select:
select po.pre_orders_id as pre_orders_id,null as product_name,po.contract_no as contract_no ,po.apply_name as apply_name ,po.apply_phone as apply_phone ,po.receive_name as receive_name, po.receive_mobile as receive_mobile, null as manager_name, po.status as status from pre_orders po where po.status=0
and po.create_date >= #{beginTime}
and po.create_date <= #{endTime}
and po.shop_id = #{shopId}
union all
select po.pre_orders_id as pre_orders_id,p.name as product_name ,po.contract_no as contract_no ,po.apply_name as apply_name ,po.apply_phone as apply_phone ,po.receive_name as receive_name, po.receive_mobile as receive_mobile, m.name as manager_name, po.status as status from pre_orders po ,product p , manager m where po.product_uuid=p.product_uuid and po.sale_manager=m.manager_id
and po.update_time >= #{beginTime}
and po.update_time <= #{endTime}
and po.shop_id = #{shopId}

select po.pre_orders_id as pre_orders_id, null as product_name,po.contract_no as contract_no ,po.apply_name as apply_name
,null as product_uuid ,null as sale_manager ,null as user_phone ,null as receive_account ,null as receive_address ,null as receive_period
,po.apply_phone as apply_phone ,po.receive_name as receive_name, po.receive_mobile as receive_mobile, null as manager_name, po.status as status from pre_orders po where po.shop_id = #{shopId} and po.status=0
and po.create_date >= #{beginTime}
and po.create_date <= #{endTime}
and po.status = #{status}
and po.apply_phone = #{applyPhone}
and po.receive_mobile = #{receiveMobile}
union all
select po.pre_orders_id as pre_orders_id,p.name as product_name ,po.contract_no as contract_no ,po.apply_name as apply_name
,po.product_uuid as product_uuid ,po.sale_manager as sale_manager ,po.user_phone as user_phone ,po.receive_account as receive_account ,po.receive_address as receive_address ,po.receive_period as receive_period
,po.apply_phone as apply_phone ,po.receive_name as receive_name, po.receive_mobile as receive_mobile, m.name as manager_name, po.status as status from pre_orders po ,product p , manager m where po.shop_id = #{shopId} and po.product_uuid=p.product_uuid and po.sale_manager=m.manager_id and po.status=1
and po.update_time >= #{beginTime}
and po.update_time <= #{endTime}
and po.status = #{status}
and po.apply_phone = #{applyPhone}
and po.receive_mobile = #{receiveMobile}
